              <?php $Skills-> the_post(); ?>
              <?php $skillInfo = get_field('skills') ?>
              <ul class="skills">
               <?php 
                  for ($x = 0; $x < count($skillInfo); $x++) {
                    echo "<li>" . $skillInfo[$x]['skill_icon'];
                     echo "<p>" . $skillInfo[$x]['skill_type'] . "</p></li>";
                  }
                ?>
                </ul>          
            <?php endwhile; ?>
            <?php wp_reset_postdata(); ?>
          <?php endif; ?>
         
        <!-- start portfolio content loop -->

          <?php $Portfolio = new WP_Query(array(
            'posts_per_page'=> -1,
            'post_type'=> 'portfolio'
          )); ?>

          <?php if ($Portfolio-> have_posts()): ?>
            <?php while ($Portfolio-> have_posts()): ?>
              <?php $Portfolio-> the_post(); ?>
                <article class="portfolio-piece">
                  <!-- images -->
                  <?php $image = get_field('image_bw'); ?>
                  <div class="portfolio-image">
                  <img class="bw" src="<?php echo $image['sizes']['large'] ?>" alt="">
                  <?php $image = get_field('image_colour'); ?>
                  <img class="colour" src="<?php echo $image['sizes']['large']; ?>" alt="">
                  </div>
                  <div class="portfolio-piece-description">
                    <!-- project name -->
                    <h3><?php echo get_field('project_name'); ?></h3>
                    <!-- project description -->
                    <p><?php echo get_field('project_description'); ?></p>
                    
                    <!--  project links-->
                    <?php while(has_sub_field('project_links')): ?>
                      <?php $links = get_field('project_links'); ?>
                      
                          <a class="button hvr-bounce-to-right" href="<?php echo $links['0']['project_link']; ?>"><?php echo $links['0']['project_link_title']; ?></a>
                       
                      <?php endwhile; ?> <!-- end project link loop -->   
                  </div>
                </article>
                
            <?php endwhile; ?> <!-- end portfolio content printing loop -->
            <?php wp_reset_postdata(); ?>
          <?php endif; ?> <!-- end portfolio loop -->
